import React, {useEffect , useState} from 'react'
import axios from 'axios'

export default function Project() {
  const [data , setData] = useState([])

  useEffect(() => {
      axios({
        url:'http://localhost:3000/api/projects'
      })
      .then((res)=>{
        setData(res.data)
      })
      .catch((err)=>{
        console.log(err)
      })
  }, [])


  return (
    <div>

    </div>
  )
}



